class Element {
  constructor(id) {
    this.id = id;
    this.textContent = '';
    this.style = {};
  }
}

class Document {
  constructor(html) {
    this.elements = {};
    const regex = /id="([^"]+)"/g;
    let match;
    while ((match = regex.exec(html)) !== null) {
      this.elements[match[1]] = new Element(match[1]);
    }
  }

  getElementById(id) {
    return this.elements[id] || null;
  }
}

class JSDOM {
  constructor(html) {
    this.window = { document: new Document(html) };
  }
}

module.exports = { JSDOM };
